}

const keysLower = Object.keys(rows[0]).map(k => k.toLowerCase());
function detectReportType(keys) {
  const isFBS = keys.some(k => k.includes('—Å–ø–æ—Å–æ–± –æ—Ç–≥—Ä—É–∑–∫–∏') || k.includes('–ø–µ—Ä–µ–≤–æ–∑—á–∏–∫') || k.includes('–Ω–∞–∑–≤–∞–Ω–∏–µ –º–µ—Ç–æ–¥–∞') || k.includes('–¥–∞—Ç–∞ –æ—Ç–≥—Ä—É–∑–∫–∏ –±–µ–∑ –ø—Ä–æ—Å—Ä–æ—á–∫–∏'));
  const isFBO = keys.some(k => k.includes('—é—Ä–∏–¥–∏—á–µ—Å–∫–æ–µ –ª–∏—Ü–æ') || k.includes('–æ—Ü–µ–Ω–∫–∞ –æ—Ç–≥—Ä—É–∑–∫–∏'));
  if (isFBS) return 'FBS';
  if (isFBO) return 'FBO';
  return 'UNKNOWN';
}
const reportType = detectReportType(keysLower);
if (reportType === 'UNKNOWN') {
  throw new Error('–ù–µ —É–¥–∞–ª–æ—Å—å –æ–ø—Ä–µ–¥–µ–ª–∏—Ç—å —Ç–∏–ø –æ—Ç—á—ë—Ç–∞ –ø–æ –∑–∞–≥–æ–ª–æ–≤–∫–∞–º');
}

function pick(obj, names, fallback = '') {
  for (const n of names) {
    if (obj[n] !== undefined && String(obj[n]).trim() !== '') return obj[n];
  }
  return fallback;
}

const records = [];
for (const r of rows) {
  let order_id, sku, quantity, price, created_at, status;
  if (reportType === 'FBO') {
    order_id   = pick(r, ['–ù–æ–º–µ—Ä –∑–∞–∫–∞–∑–∞']);
    sku        = pick(r, ['–ê—Ä—Ç–∏–∫—É–ª', 'OZON id']);
    quantity   = Number(pick(r, ['–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ'], 1));
    price      = Number(pick(r, ['–í–∞—à–∞ —Ü–µ–Ω–∞', '–°—É–º–º–∞ –æ—Ç–ø—Ä–∞–≤–ª–µ–Ω–∏—è'], 0));
    created_at = pick(r, ['–ü—Ä–∏–Ω—è—Ç –≤ –æ–±—Ä–∞–±–æ—Ç–∫—É','–î–∞—Ç–∞ –æ—Ç–≥—Ä—É–∑–∫–∏','–§–∞–∫—Ç–∏—á–µ—Å–∫–∞—è –¥–∞—Ç–∞ –ø–µ—Ä–µ–¥–∞—á–∏ –≤ –¥–æ—Å—Ç–∞–≤–∫—É']);
    status     = pick(r, ['–°—Ç–∞—Ç—É—Å'], '');
  } else {
    order_id   = pick(r, ['–ù–æ–º–µ—Ä –∑–∞–∫–∞–∑–∞','‚Ññ –∑–∞–∫–∞–∑–∞']);
    sku        = pick(r, ['–ê—Ä—Ç–∏–∫—É–ª','–ê—Ä—Ç–∏–∫—É–ª –ø—Ä–æ–¥–∞–≤—Ü–∞','OZON id']);
    quantity   = Number(pick(r, ['–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ','–ö–æ–ª-–≤–æ'], 1));
    price      = Number(pick(r, ['–í–∞—à–∞ —Ü–µ–Ω–∞','–°—É–º–º–∞ –æ—Ç–ø—Ä–∞–≤–ª–µ–Ω–∏—è'], 0));
    created_at = pick(r, ['–ü—Ä–∏–Ω—è—Ç –≤ –æ–±—Ä–∞–±–æ—Ç–∫—É','–î–∞—Ç–∞ –æ—Ç–≥—Ä—É–∑–∫–∏','–î–∞—Ç–∞ –æ—Ç–≥—Ä—É–∑–∫–∏ –±–µ–∑ –ø—Ä–æ—Å—Ä–æ—á–∫–∏']);
    status     = pick(r, ['–°—Ç–∞—Ç—É—Å'], '');
  }
  if (!order_id || !sku) continue;
  records.push({
    order_id,
    sku,
    quantity: Number.isFinite(quantity) ? quantity : 1,
    price: Number.isFinite(price) ? price : 0,
    created_at,
    status: String(status || '').toLowerCase(),
  });
}

const dates = new Set();
for (const rec of records) {
  if (!rec.created_at) continue;
  const d = new Date(rec.created_at);
  if (isNaN(d)) continue;
  const msk = new Date(d.getTime() + 3 * 60 * 60 * 1000);
  dates.add(msk.toISOString().split('T')[0]);
}

const availableDates = Array.from(dates).sort();
