// === –ß–¢–ï–ù–ò–ï –ü–û–°–õ–ï Extract from File ===
// –ë–µ—Ä—ë–º –≤—Å–µ items –∏–∑ Extract from File: –∫–∞–∂–¥—ã–π item ‚Äî —ç—Ç–æ –æ–±—ä–µ–∫—Ç —Å–æ —Å—Ç–æ–ª–±—Ü–∞–º–∏
const rawItems = $input.all().map(i => i.json.row ?? i.json).filter(Boolean);
if (rawItems.length === 0) {
  throw new Error('Extract from File –Ω–µ –≤–µ—Ä–Ω—É–ª –Ω–∏ –æ–¥–Ω–æ–π —Å—Ç—Ä–æ–∫–∏');
