// --- –£—Ç–∏–ª–∏—Ç—ã –Ω–æ—Ä–º–∞–ª–∏–∑–∞—Ü–∏–∏ ---
const RE_BOM = /^\uFEFF/;
const RE_QUOTES_AROUND = /^"+|"+$/g;
const RE_NBSP = /\u00A0/g; // –Ω–µ—Ä–∞–∑—Ä—ã–≤–Ω—ã–π –ø—Ä–æ–±–µ–ª
const RE_MULTI_SPACE = /\s+/g;

function cleanKey(k) {
  if (k == null) return '';
  return String(k)
    .replace(RE_BOM, '')
    .replace(RE_NBSP, ' ')
    .replace(RE_QUOTES_AROUND, '')
    .trim()
    .replace(RE_MULTI_SPACE, ' ');
}
function cleanVal(v) {
  if (v == null) return '';
  return String(v).replace(RE_BOM, '').trim();
}
function toNumberLoose(v, fallback = 0) {
  if (v == null) return fallback;
  const s = String(v).replace(/\s/g, '').replace(',', '.');
  const n = Number(s);
  return Number.isFinite(n) ? n : fallback;
}

// –ü–æ—Å—Ç—Ä–æ–∏–º –Ω–æ—Ä–º–∞–ª–∏–∑–æ–≤–∞–Ω–Ω—ã–µ —Å—Ç—Ä–æ–∫–∏ + –∫–∞—Ä—Ç—É –∫–ª—é—á–µ–π (lowercase -> –∏—Å—Ö–æ–¥–Ω—ã–π)
function normalizeRow(row) {
  const out = {};
  for (const k of Object.keys(row)) {
    const ck = cleanKey(k);
    out[ck] = cleanVal(row[k]);
  }
  return out;
}

const rows = rawItems.map(normalizeRow);

// –°–ø–∏—Å–æ–∫ –≤—Å–µ—Ö –∫–ª—é—á–µ–π (–Ω–æ—Ä–º–∞–ª–∏–∑–æ–≤–∞–Ω–Ω—ã—Ö)
const allKeys = new Set();
rows.slice(0, 5).forEach(r => Object.keys(r).forEach(k => allKeys.add(k)));
const keysLower = Array.from(allKeys).map(k => k.toLowerCase());

// –î–µ—Ç–µ–∫—Ç —Ç–∏–ø–∞ –æ—Ç—á—ë—Ç–∞ –ø–æ –ø–æ–¥—Å—Ç—Ä–æ–∫–∞–º –≤ –∑–∞–≥–æ–ª–æ–≤–∫–∞—Ö
function detectReportType(keysLowerArr) {
  const has = (frag) => keysLowerArr.some(k => k.includes(frag));
  const isFBS =
    has('—Å–ø–æ—Å–æ–± –æ—Ç–≥—Ä—É–∑–∫–∏') || has('–ø–µ—Ä–µ–≤–æ–∑—á–∏–∫') ||
    has('–Ω–∞–∑–≤–∞–Ω–∏–µ –º–µ—Ç–æ–¥–∞') || has('–¥–∞—Ç–∞ –æ—Ç–≥—Ä—É–∑–∫–∏ –±–µ–∑ –ø—Ä–æ—Å—Ä–æ—á–∫–∏');
  const isFBO =
    has('—é—Ä–∏–¥–∏—á–µ—Å–∫–æ–µ –ª–∏—Ü–æ') || has('–æ—Ü–µ–Ω–∫–∞ –æ—Ç–≥—Ä—É–∑–∫–∏') ||
    has('–ø—Ä–∏–Ω—è—Ç –≤ –æ–±—Ä–∞–±–æ—Ç–∫—É') || has('–¥–∞—Ç–∞ –æ—Ç–≥—Ä—É–∑–∫–∏');
  if (isFBS && !isFBO) return 'FBS';
  if (isFBO && !isFBS) return 'FBO';
  // –µ—Å–ª–∏ –≤—Å—Ç—Ä–µ—á–∞—é—Ç—Å—è –æ–±–∞ –Ω–∞–±–æ—Ä–∞, –ø–æ–ø—Ä–æ–±—É–µ–º –ø–æ –ø–æ–ª—è–º –∑–∞–∫–∞–∑–∞
  if (has('–Ω–æ–º–µ—Ä –∑–∞–∫–∞–∑–∞') || has('‚Ññ –∑–∞–∫–∞–∑–∞')) return 'FBO';
  return 'FBO'; // –ø–æ —Ç–≤–æ–∏–º –ø—Ä–∏–º–µ—Ä–∞–º —á–∞—â–µ FBO; fallback
}

const reportType = detectReportType(keysLower);

// –ú—è–≥–∫–∏–π –≤—ã–±–æ—Ä –∑–Ω–∞—á–µ–Ω–∏—è: –ø—ã—Ç–∞–µ–º—Å—è –ø–æ —Ç–æ—á–Ω–æ–º—É –∏–º–µ–Ω–∏,
// –∑–∞—Ç–µ–º –ø–æ case-insensitive —Å–æ–≤–ø–∞–¥–µ–Ω–∏—é —Å—Ä–µ–¥–∏ –∫–ª—é—á–µ–π —Å—Ç—Ä–æ–∫–∏.
function pick(record, candidates, fallback = '') {
  for (const name of candidates) {
    const c = cleanKey(name);
    if (record[c] !== undefined && String(record[c]).trim() !== '') {
      return record[c];
    }
  }
  // case-insensitive –ø–æ–∏—Å–∫
  const recKeys = Object.keys(record);
  for (const name of candidates) {
    const cLower = cleanKey(name).toLowerCase();
    const found = recKeys.find(k => k.toLowerCase() === cLower);
    if (found && String(record[found]).trim() !== '') {
      return record[found];
    }
  }
  return fallback;
}

// –ù–æ—Ä–º–∞–ª–∏–∑–∞—Ü–∏—è –∑–∞–ø–∏—Å–µ–π
const records = [];
for (const r of rows) {
  let order_id, sku, quantity, price, created_at, status;

  if (reportType === 'FBO') {
    order_id   = pick(r, ['–ù–æ–º–µ—Ä –∑–∞–∫–∞–∑–∞']);
    sku        = pick(r, ['–ê—Ä—Ç–∏–∫—É–ª', 'OZON id', 'OZON ID']);
    quantity   = toNumberLoose(pick(r, ['–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ'], 1), 1);
    price      = toNumberLoose(pick(r, ['–í–∞—à–∞ —Ü–µ–Ω–∞', '–°—É–º–º–∞ –æ—Ç–ø—Ä–∞–≤–ª–µ–Ω–∏—è'], 0), 0);
    created_at = pick(r, ['–ü—Ä–∏–Ω—è—Ç –≤ –æ–±—Ä–∞–±–æ—Ç–∫—É', '–î–∞—Ç–∞ –æ—Ç–≥—Ä—É–∑–∫–∏', '–§–∞–∫—Ç–∏—á–µ—Å–∫–∞—è –¥–∞—Ç–∞ –ø–µ—Ä–µ–¥–∞—á–∏ –≤ –¥–æ—Å—Ç–∞–≤–∫—É']);
    status     = pick(r, ['–°—Ç–∞—Ç—É—Å'], '');
  } else {
    order_id   = pick(r, ['–ù–æ–º–µ—Ä –∑–∞–∫–∞–∑–∞', '‚Ññ –∑–∞–∫–∞–∑–∞']);
    sku        = pick(r, ['–ê—Ä—Ç–∏–∫—É–ª –ø—Ä–æ–¥–∞–≤—Ü–∞', '–ê—Ä—Ç–∏–∫—É–ª', 'OZON id', 'OZON ID']);
    quantity   = toNumberLoose(pick(r, ['–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ', '–ö–æ–ª-–≤–æ'], 1), 1);
    price      = toNumberLoose(pick(r, ['–í–∞—à–∞ —Ü–µ–Ω–∞', '–°—É–º–º–∞ –æ—Ç–ø—Ä–∞–≤–ª–µ–Ω–∏—è'], 0), 0);
    created_at = pick(r, ['–ü—Ä–∏–Ω—è—Ç –≤ –æ–±—Ä–∞–±–æ—Ç–∫—É', '–î–∞—Ç–∞ –æ—Ç–≥—Ä—É–∑–∫–∏', '–î–∞—Ç–∞ –æ—Ç–≥—Ä—É–∑–∫–∏ –±–µ–∑ –ø—Ä–æ—Å—Ä–æ—á–∫–∏']);
    status     = pick(r, ['–°—Ç–∞—Ç—É—Å'], '');
  }

  if (!order_id || !sku) continue;

  records.push({
    order_id: cleanVal(order_id),
    sku: cleanVal(sku),
    quantity,
    price,
    created_at: cleanVal(created_at),
    status: String(status || '').toLowerCase(),
  });
}

// –î–∞—Ç—ã –¥–ª—è –∫–ª–∞–≤–∏–∞—Ç—É—Ä—ã (–∏—Å–ø–æ–ª—å–∑—É–µ–º parseAsMsk –¥–ª—è –∫–æ–Ω—Å–∏—Å—Ç–µ–Ω—Ç–Ω–æ—Å—Ç–∏)
const dates = new Set();
for (const rec of records) {
  const msk = parseAsMsk(rec.created_at);
  if (!msk) continue;
  dates.add(msk.toISOString().split('T')[0]);
}

const availableDates = Array.from(dates).sort();
const chatId = $('Extract User Data').first().json.chat_id;
