// –í—Å—Ç–∞–≤–∏—Ç—å —Å–≤–µ—Ä—Ö—É (–ø–µ—Ä–µ–¥ filterRecordsByDateTime):
function parseAsMsk(dateStr) {
  if (!dateStr) return null;
  let d = dateStr;
  // '2025-09-30 07:40:08' -> '2025-09-30T07:40:08Z' (–∫–∞–∫ –≤ Parse CSV)
  if (/^\d{4}-\d{2}-\d{2} \d{2}:\d{2}:\d{2}$/.test(d)) {
    d = d.replace(' ', 'T') + 'Z';
  }
  const base = new Date(d);
  if (isNaN(base)) return null;
  // –ø—Ä–∏–≤–æ–¥–∏–º –∫ –ú–°–ö (+3—á)
  return new Date(base.getTime() + 3 * 60 * 60 * 1000);
}

function filterRecordsByDateTime(records, date, startTime, endTime) {
  return records.filter(record => {
    const mskDate = parseAsMsk(record.created_at);
    if (!mskDate) return false;
    const recordDateStr = mskDate.toISOString().split('T')[0];
    const recordTimeStr = mskDate.toTimeString().split(' ')[0].substring(0, 5);
    return recordDateStr === date && recordTimeStr >= startTime && recordTimeStr <= endTime;
  });
}

function calculateStatistics(records, date, startTime, endTime) {
  const filteredRecords = filterRecordsByDateTime(records, date, startTime, endTime);

  if (filteredRecords.length === 0) {
    return {
      date, startTime, endTime,
      totalOrders: 0,
      totalCancellations: 0,
      totalRevenue: 0,
      skuStats: {},
      message: '–ù–µ—Ç –¥–∞–Ω–Ω—ã—Ö –∑–∞ —É–∫–∞–∑–∞–Ω–Ω—ã–π –ø–µ—Ä–∏–æ–¥'
    };
  }

  const statsBySku = {};
  const revenueStatuses = ['–¥–æ—Å—Ç–∞–≤–ª–µ–Ω', '–¥–æ—Å—Ç–∞–≤–ª—è–µ—Ç—Å—è', '–æ–∂–∏–¥–∞–µ—Ç —Å–±–æ—Ä–∫–∏', '–æ–∂–∏–¥–∞–µ—Ç –æ—Ç–≥—Ä—É–∑–∫–∏'];
  const cancelStatuses = ['–æ—Ç–º–µ–Ω—ë–Ω', '–æ—Ç–º–µ–Ω–µ–Ω', '–≤–æ–∑–≤—Ä–∞—Ç'];

  filteredRecords.forEach(record => {
    const sku = record.sku;
    const status = record.status.toLowerCase();
    const quantity = record.quantity || 1;
    const price = record.price || 0;

    if (!statsBySku[sku]) {
      statsBySku[sku] = {
        totalOrders: 0,
        cancellations: 0,
        totalRevenue: 0,
        weightedPriceSum: 0,
        weightedQuantitySum: 0,
        avgPrice: 0
      };
    }

    statsBySku[sku].totalOrders += quantity;

    if (cancelStatuses.some(s => status.includes(s))) {
      statsBySku[sku].cancellations += quantity;
    }

    if (revenueStatuses.some(s => status.includes(s))) {
      statsBySku[sku].totalRevenue += price * quantity;
      statsBySku[sku].weightedPriceSum += price * quantity;
      statsBySku[sku].weightedQuantitySum += quantity;
    }
  });

  Object.keys(statsBySku).forEach(sku => {
    const stats = statsBySku[sku];
    if (stats.weightedQuantitySum > 0) {
      stats.avgPrice = stats.weightedPriceSum / stats.weightedQuantitySum;
    }
  });

  let totalOrders = 0, totalCancellations = 0, totalRevenue = 0;
  Object.values(statsBySku).forEach(stats => {
    totalOrders += stats.totalOrders;
    totalCancellations += stats.cancellations;
    totalRevenue += stats.totalRevenue;
  });

  return { date, startTime, endTime, totalOrders, totalCancellations, totalRevenue, skuStats: statsBySku };
}

function formatStatisticsMessage(stats) {
  let message = `üìä <b>–°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ –∑–∞–∫–∞–∑–æ–≤</b>\n\n`;
  message += `üìÖ –î–∞—Ç–∞: ${stats.date}\n`;
  message += `‚è∞ –í—Ä–µ–º—è: ${stats.startTime} - ${stats.endTime}\n\n`;

  if (stats.totalOrders === 0) {
    message += stats.message || '–ù–µ—Ç –¥–∞–Ω–Ω—ã—Ö';
    return message;
  }

  const sortedSkus = Object.keys(stats.skuStats).sort();
  sortedSkus.forEach(sku => {
    const skuStats = stats.skuStats[sku];
    message += `<b>${sku}</b>\n`;
    message += `  ‚Ä¢ –ó–∞–∫–∞–∑–æ–≤: ${skuStats.totalOrders}\n`;
    message += `  ‚Ä¢ –û—Ç–º–µ–Ω: ${skuStats.cancellations}\n`;
    message += `  ‚Ä¢ –°—Ä–µ–¥–Ω—è—è —Ü–µ–Ω–∞: ${skuStats.avgPrice.toFixed(2)} ‚ÇΩ\n`;
    message += `  ‚Ä¢ –°—É–º–º–∞: ${skuStats.totalRevenue.toFixed(2)} ‚ÇΩ\n\n`;
  });

  message += `<b>–ò–¢–û–ì–û:</b>\n`;
  message += `  ‚Ä¢ –í—Å–µ–≥–æ –∑–∞–∫–∞–∑–æ–≤: ${stats.totalOrders}\n`;
  message += `  ‚Ä¢ –í—Å–µ–≥–æ –æ—Ç–º–µ–Ω: ${stats.totalCancellations}\n`;
  message += `  ‚Ä¢ –û–±—â–∞—è —Å—É–º–º–∞: ${stats.totalRevenue.toFixed(2)} ‚ÇΩ\n`;

  return message;
}

// Main execution
const parsedData = $input.first().json;
const date = $json.selectedDate || parsedData.availableDates[0];
const startTime = $json.startTime || '00:00';
const endTime = $json.endTime || '23:59';

