// Address inputs by index with fallback for compatibility
const fromPort0 = $input.all(0); // Date selection from Handle Date Callback
const fromPort1 = $input.all(1); // Cached data from Get Cached Data
const dateSelection = (fromPort0[0]?.json) || $input.first().json;
const cachedRaw = (fromPort1[0]?.json?.value) ?? $input.last().json?.value;

let cachedData = {};
try {
