// Parse CSV line handling quotes
function parseCSVLine(line) {
  const values = [];
  let current = '';
  let inQuotes = false;

  for (let i = 0; i < line.length; i++) {
    const char = line[i];

    if (char === '"') {
      if (inQuotes && line[i + 1] === '"') {
        current += '"';
        i++;
      } else {
        inQuotes = !inQuotes;
      }
    } else if (char === ',' && !inQuotes) {
      values.push(current.trim());
      current = '';
    } else {
      current += char;
    }
  }

  values.push(current.trim());
  return values;
}

function detectReportType(headers) {
  const headersLower = headers.map(h => h.toLowerCase());
  if (headersLower.some(h => h.includes('–Ω–æ–º–µ—Ä –∑–∞–∫–∞–∑–∞'))) return 'FBO';
  if (headersLower.some(h => h.includes('‚Ññ –∑–∞–∫–∞–∑–∞'))) return 'FBS';
  return 'UNKNOWN';
}

function findValue(record, possibleKeys, fallback = '') {
  for (const key of possibleKeys) {
    if (record[key] !== undefined && record[key] !== '') {
      return record[key];
    }
  }
  return fallback;
}

function normalizeRecord(headers, values, reportType) {
  const record = {};
  headers.forEach((header, idx) => {
    record[header] = values[idx] || '';
  });

  let orderId, sku, quantity, price, createdAt, status;

  if (reportType === 'FBO') {
    orderId = findValue(record, ['–ù–æ–º–µ—Ä –∑–∞–∫–∞–∑–∞'], values[0]);
    sku = findValue(record, ['–ê—Ä—Ç–∏–∫—É–ª'], values[6]);
    quantity = parseInt(findValue(record, ['–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ'], values[8]) || '1');
    price = parseFloat(findValue(record, ['–¶–µ–Ω–∞'], values[13]) || '0');
    createdAt = findValue(record, ['–î–∞—Ç–∞ —Å–æ–∑–¥–∞–Ω–∏—è'], values[15]);
    status = findValue(record, ['–°—Ç–∞—Ç—É—Å'], values[16]);
  } else {
    orderId = findValue(record, ['‚Ññ –∑–∞–∫–∞–∑–∞'], values[0]);
    sku = findValue(record, ['–ê—Ä—Ç–∏–∫—É–ª –ø—Ä–æ–¥–∞–≤—Ü–∞', '–ê—Ä—Ç–∏–∫—É–ª'], values[1]);
    quantity = parseInt(findValue(record, ['–ö–æ–ª-–≤–æ'], values[2]) || '1');
    price = parseFloat(findValue(record, ['–¶–µ–Ω–∞'], values[3]) || '0');
    createdAt = findValue(record, ['–î–∞—Ç–∞ —Å–æ–∑–¥–∞–Ω–∏—è'], values[4]);
    status = findValue(record, ['–°—Ç–∞—Ç—É—Å'], values[5]);
  }

  if (!orderId || !sku) return null;

  return {
    order_id: orderId,
    sku: sku,
    quantity: isNaN(quantity) ? 1 : quantity,
    price: isNaN(price) ? 0 : price,
    created_at: createdAt,
    status: status ? status.toLowerCase() : ''
  };
}

