    message += `  ‚Ä¢ –°—Ä–µ–¥–Ω—è—è —Ü–µ–Ω–∞: ${skuStats.avgPrice.toFixed(2)} ‚ÇΩ\n`;
    message += `  ‚Ä¢ –°—É–º–º–∞: ${skuStats.totalRevenue.toFixed(2)} ‚ÇΩ\n\n`;
  });

  message += `**–ò–¢–û–ì–û:**\n`;
  message += `  ‚Ä¢ –í—Å–µ–≥–æ –∑–∞–∫–∞–∑–æ–≤: ${stats.totalOrders}\n`;
  message += `  ‚Ä¢ –í—Å–µ–≥–æ –æ—Ç–º–µ–Ω: ${stats.totalCancellations}\n`;
  message += `  ‚Ä¢ –û–±—â–∞—è —Å—É–º–º–∞: ${stats.totalRevenue.toFixed(2)} ‚ÇΩ\n`;

  return message;
}

